package aadl2upaal.aadl;

public class AVar {
	private String name;
	private String type;

	public AVar(String name, String type) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.type = type;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getType() {
		return type;
	}

	public void setType(String type) {
		this.type = type;
	}

}
